package org.topicquests.os.asr.linkgrammar;

import java.io.BufferedReader;
import java.io.InputStream;
import java.io.InputStreamReader;
import java.io.OutputStreamWriter;
import java.io.PrintWriter;
import java.net.Socket;
import java.util.*;

import org.topicquests.support.ResultPojo;
import org.topicquests.support.api.IResult;

import net.minidev.json.JSONObject;

/**
 * @author jackpark
 *
 */
public class Fetcher {
	private LinkGrammarClientEnvironment environment;
	private Socket socket;
	private Object SYNCH = new Object();


	/**
	 * 
	 */
	public Fetcher(LinkGrammarClientEnvironment env) {
		environment = env;
		
	}
	
	void openSocket() throws Exception {
		String h = environment.getStringProperty("LGHost");
		String p = environment.getStringProperty("LGPort");
		socket = new Socket(h, Integer.parseInt(p));
	}
	
	void closeSocket() throws Exception {
		socket.close();
		socket = null;
	}
	
	public IResult parseSentence(String sentence) {
		IResult result = new ResultPojo();
		try {
			openSocket();
			OutputStreamWriter osw = new OutputStreamWriter(socket.getOutputStream(), "UTF-8");
			PrintWriter pw = new PrintWriter(osw, true);
			pw.println("text:"+sentence);
			InputStream input = socket.getInputStream();
			BufferedReader rd = new BufferedReader(new InputStreamReader(input));
			StringBuilder buf = new StringBuilder();
			synchronized(SYNCH) {
				while (!rd.ready()) {
					System.out.println("W");
					try {
						SYNCH.wait(50);
					} catch (Exception e) {
						e.printStackTrace();
					}
				}
			}

			String line;
			while ((line = rd.readLine()) != null) {
				buf.append(line + '\n');
			}
			result.setResultObject(buf.toString());
			closeSocket();
		} catch (Exception e) {
			environment.logError(e.getMessage(), e);
			result.addErrorString(e.getMessage());
		}
		
		return result;
	}
	
	public IResult processSentences(List<String>sentences) {
		IResult result = new ResultPojo();
		List<JSONObject>parses = new ArrayList<JSONObject>();
		result.setResultObject(parses);
		StringBuilder buf = new StringBuilder();
		InputStream input;
		OutputStreamWriter osw;
		BufferedReader rd;
		PrintWriter pw;
		String line;
		JSONObject jo;
		try {
			Iterator<String>itr = sentences.iterator();
			String sentence;
			while (itr.hasNext()) {
				sentence = itr.next();
				System.out.println("X "+sentence);
				openSocket();
				jo = new JSONObject();
				jo.put("sent", sentence);
				osw = new OutputStreamWriter(socket.getOutputStream(), "UTF-8");
				pw = new PrintWriter(osw, true);
				pw.println("text:"+sentence);
				input = socket.getInputStream();
				rd = new BufferedReader(new InputStreamReader(input));
				synchronized(SYNCH) {
					while (!rd.ready()) {
						System.out.println("W");
						try {
							SYNCH.wait(1000);
						} catch (Exception e) {
							e.printStackTrace();
						}
					}
				}
				buf.setLength(0);
				System.out.println("y "+buf.toString());
				while ((line = rd.readLine()) != null) {
					buf.append(line + '\n');
				}
				jo.put("parse", buf.toString());
				parses.add(jo);
				System.out.println("Z "+jo.toJSONString());
				closeSocket();
			}
			
			
		} catch (Exception e) {
			environment.logError(e.getMessage(), e);
			result.addErrorString(e.getMessage());
		}		
		return result;
	}
}

